import React, { useState } from 'react';
import MessageTemplates from './MessageTemplates';
import RichTextEditor from './RichTextEditor';
import './ChatArea.css';

const ChatArea = ({ selectedContact, currentMessage, setCurrentMessage, onAISuggest }) => {
  const [showTemplates, setShowTemplates] = useState(false);

  if (!selectedContact) {
    return (
      <div className="chat-area">
        <div className="no-contact-selected">
          <div className="no-contact-icon">
            <svg width="48" height="48" viewBox="0 0 24 24" fill="none">
              <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2z" stroke="currentColor" strokeWidth="2"/>
            </svg>
          </div>
          <h3>Select a contact to start messaging</h3>
          <p>Choose a contact from the sidebar to begin your conversation</p>
        </div>
      </div>
    );
  }

  const handleSendMessage = () => {
    if (currentMessage.trim() && selectedContact) {
      // Format phone number (assuming it's stored in contact)
      const phoneNumber = selectedContact.phone || '1234567890'; // Default for demo
      
      // Encode the message for URL
      const encodedMessage = encodeURIComponent(currentMessage);
      
      // Create WhatsApp URL
      const whatsappUrl = `https://wa.me/${phoneNumber}?text=${encodedMessage}`;
      
      // Open WhatsApp
      window.open(whatsappUrl, '_blank');
      
      // Clear the message after sending
      setCurrentMessage('');
      
      // Log for demo purposes
      console.log('Sending via WhatsApp:', currentMessage, 'to', selectedContact.name);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleTemplateSelect = (template) => {
    setCurrentMessage(template.content);
    setShowTemplates(false);
  };

  return (
    <div className="chat-area">
      {showTemplates ? (
        <MessageTemplates 
          contact={selectedContact}
          onTemplateSelect={handleTemplateSelect}
          onClose={() => setShowTemplates(false)}
        />
      ) : (
        <>
          <div className="chat-header">
            <div className="contact-info">
              <div className="contact-avatar">
                <span>{selectedContact.initials}</span>
              </div>
              <div className="contact-details">
                <h3>{selectedContact.name}</h3>
                <span className="contact-status">{selectedContact.status}</span>
              </div>
            </div>
            
            <div className="chat-actions">
              <button 
                className="template-btn"
                onClick={() => setShowTemplates(true)}
              >
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                  <path d="M14 2H6a2 2 0 0 0-2 2v16a2 2 0 0 0 2 2h12a2 2 0 0 0 2-2V8z" stroke="currentColor" strokeWidth="2"/>
                  <polyline points="14,2 14,8 20,8" stroke="currentColor" strokeWidth="2"/>
                  <line x1="16" y1="13" x2="8" y2="13" stroke="currentColor" strokeWidth="2"/>
                  <line x1="16" y1="17" x2="8" y2="17" stroke="currentColor" strokeWidth="2"/>
                  <polyline points="10,9 9,9 8,9" stroke="currentColor" strokeWidth="2"/>
                </svg>
                Templates
              </button>
            </div>
          </div>

          <div className="chat-messages">
            <div className="message-day-separator">
              <span>Today</span>
            </div>
            
            <div className="message sent">
              <div className="message-content">
                Hi {selectedContact.name}, this is a friendly reminder about your appointment scheduled for tomorrow at 2:00 PM. Please reply to confirm or let us know if you need to reschedule.
              </div>
              <div className="message-time">2:30 PM</div>
            </div>

            <div className="message received">
              <div className="message-content">
                Thank you for the reminder! I'll be there on time.
              </div>
              <div className="message-time">2:45 PM</div>
            </div>
          </div>

          <div className="chat-input-area">
            <div className="input-container">
              <RichTextEditor
                value={currentMessage}
                onChange={setCurrentMessage}
                placeholder={`Message ${selectedContact.name}...`}
              />
              
              <div className="input-actions">
                <div className="input-tools">
                  <button 
                    className="ai-suggest-btn"
                    onClick={() => onAISuggest(currentMessage)}
                    title="AI Suggest"
                  >
                    <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                      <path d="M12 2L2 7L12 12L22 7L12 2Z" stroke="currentColor" strokeWidth="2"/>
                      <path d="M2 17L12 22L22 17" stroke="currentColor" strokeWidth="2"/>
                      <path d="M2 12L12 17L22 12" stroke="currentColor" strokeWidth="2"/>
                    </svg>
                    AI Suggest
                  </button>
                </div>
                
                <button 
                  className="send-btn"
                  onClick={handleSendMessage}
                  disabled={!currentMessage.trim()}
                >
                  <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                    <path d="M22 2L11 13" stroke="currentColor" strokeWidth="2"/>
                    <polygon points="22,2 15,22 11,13 2,9 22,2" stroke="currentColor" strokeWidth="2"/>
                  </svg>
                  Send via WhatsApp
                </button>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default ChatArea;
