import React, { useState } from 'react';
import './DashboardSidebar.css';

const CustomDropdown = ({ value, onChange, options }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleSelect = (optionValue) => {
    onChange(optionValue);
    setIsOpen(false);
  };

  return (
    <div className="custom-dropdown">
      <button 
        className="dropdown-trigger"
        onClick={() => setIsOpen(!isOpen)}
      >
        <span>{value}</span>
        <svg 
          className={`dropdown-arrow ${isOpen ? 'open' : ''}`}
          width="16" 
          height="16" 
          viewBox="0 0 24 24" 
          fill="none"
        >
          <polyline points="6,9 12,15 18,9" stroke="currentColor" strokeWidth="2"/>
        </svg>
      </button>
      
      {isOpen && (
        <div className="dropdown-menu">
          {options.map((option) => (
            <div
              key={option.value}
              className={`dropdown-option ${value === option.value ? 'selected' : ''}`}
              onClick={() => handleSelect(option.value)}
            >
              <span>{option.label}</span>
              {value === option.value && (
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                  <polyline points="20,6 9,17 4,12" stroke="currentColor" strokeWidth="2"/>
                </svg>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const DashboardSidebar = ({ selectedContact, onContactSelect }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFilter, setSelectedFilter] = useState('All Contacts');

  const contacts = [
    {
      id: 1,
      name: 'Sarah Johnson',
      initials: 'SJ',
      status: 'Active',
      type: 'patient',
      phone: '1234567890'
    },
    {
      id: 2,
      name: 'Michael Chen',
      initials: 'MC',
      status: 'Active',
      type: 'patient',
      phone: '1234567891'
    },
    {
      id: 3,
      name: 'Emily Rodriguez',
      initials: 'ER',
      status: 'Patient',
      type: 'patient',
      phone: '1234567892'
    },
    {
      id: 4,
      name: 'David Thompson',
      initials: 'DT',
      status: 'Doctor',
      type: 'doctor',
      phone: '1234567893'
    },
    {
      id: 5,
      name: 'Lisa Anderson',
      initials: 'LA',
      status: 'Active',
      type: 'patient',
      phone: '1234567894'
    },
    {
      id: 6,
      name: 'James Wilson',
      initials: 'JW',
      status: 'Active',
      type: 'patient',
      phone: '1234567895'
    }
  ];

  const filteredContacts = contacts.filter(contact => {
    const matchesSearch = contact.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesFilter = selectedFilter === 'All Contacts' || 
                         (selectedFilter === 'Active Patients' && contact.status === 'Active') ||
                         (selectedFilter === 'Referred by Doctor' && contact.type === 'doctor') ||
                         (selectedFilter === 'Referred by Patient' && contact.type === 'patient');
    return matchesSearch && matchesFilter;
  });

  const getStatusColor = (status) => {
    switch (status) {
      case 'Active': return '#10b981';
      case 'Patient': return '#6366f1';
      case 'Doctor': return '#f59e0b';
      default: return '#6b7280';
    }
  };

  return (
    <div className="dashboard-sidebar">


      {/* Search */}
      <div className="search-section">
        <div className="search-input-container">
          <svg className="search-icon" width="16" height="16" viewBox="0 0 24 24" fill="none">
            <circle cx="11" cy="11" r="8" stroke="currentColor" strokeWidth="2"/>
            <path d="m21 21-4.35-4.35" stroke="currentColor" strokeWidth="2"/>
          </svg>
          <input
            type="text"
            placeholder="Search contacts..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>
      </div>

      {/* Filter Dropdown */}
      <div className="filter-section">
        <CustomDropdown 
          value={selectedFilter}
          onChange={setSelectedFilter}
          options={[
            { value: 'All Contacts', label: 'All Contacts' },
            { value: 'Active Patients', label: 'Active Patients' },
            { value: 'Referred by Doctor', label: 'Referred by Doctor' },
            { value: 'Referred by Patient', label: 'Referred by Patient' }
          ]}
        />
      </div>

      {/* Contacts List */}
      <div className="contacts-list">
        {filteredContacts.map(contact => (
          <div
            key={contact.id}
            className={`contact-item ${selectedContact?.id === contact.id ? 'selected' : ''}`}
            onClick={() => onContactSelect(contact)}
          >
            <div className="contact-avatar">
              <span className="contact-initials">{contact.initials}</span>
              <div 
                className="contact-status-dot"
                style={{ backgroundColor: getStatusColor(contact.status) }}
              />
            </div>
            
            <div className="contact-info">
              <div className="contact-name">{contact.name}</div>
              <div 
                className="contact-status"
                style={{ color: getStatusColor(contact.status) }}
              >
                {contact.status}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardSidebar;
