import React, { useState } from 'react';
import './MessageTemplates.css';

const MessageTemplates = ({ contact, onTemplateSelect, onClose }) => {
  const [selectedTemplate, setSelectedTemplate] = useState(null);

  const templates = [
    {
      id: 1,
      title: 'Appointment Reminder',
      content: `Hi {ClientName}, this is a friendly reminder about your appointment scheduled for {Date} at {Time}. Please reply to confirm or let us know if you need to reschedule. Looking forward to seeing you!`,
      category: 'appointment',
      variables: ['{ClientName}', '{Date}', '{Time}']
    },
    {
      id: 2,
      title: 'Follow-up Care',
      content: `Hello {ClientName}, we hope you're doing well! This is a follow-up message regarding your recent visit. Please don't hesitate to reach out if you have any questions or concerns.`,
      category: 'followup',
      variables: ['{ClientName}']
    },
    {
      id: 3,
      title: 'Birthday Wishes',
      content: `Happy Birthday, {ClientName}! üéâ Wishing you a wonderful day filled with joy and celebration. Thank you for being a valued client. We hope this year brings you health and happiness!`,
      category: 'birthday',
      variables: ['{ClientName}']
    },
    {
      id: 4,
      title: 'Welcome New Client',
      content: `Welcome to {Organization}, {ClientName}! We're thrilled to have you with us. Our team is dedicated to providing you with exceptional care and service. Please feel free to reach out if you have any questions.`,
      category: 'welcome',
      variables: ['{Organization}', '{ClientName}']
    }
  ];

  const handleTemplateClick = (template) => {
    setSelectedTemplate(template);
  };

  const handleUseTemplate = () => {
    if (selectedTemplate) {
      // Replace variables with actual values
      let processedContent = selectedTemplate.content;
      processedContent = processedContent.replace('{ClientName}', contact.name);
      processedContent = processedContent.replace('{Organization}', 'Smith Medical Clinic');
      processedContent = processedContent.replace('{Date}', 'tomorrow');
      processedContent = processedContent.replace('{Time}', '2:00 PM');
      
      onTemplateSelect({
        ...selectedTemplate,
        content: processedContent
      });
    }
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'appointment':
        return 'üìÖ';
      case 'followup':
        return 'üí¨';
      case 'birthday':
        return 'üéÇ';
      case 'welcome':
        return 'üëã';
      default:
        return 'üìù';
    }
  };

  return (
    <div className="message-templates">
      <div className="templates-header">
        <div className="header-content">
          <h2>Pre-defined Messages</h2>
          <p>Select messages to send to {contact.name}</p>
        </div>
        
        <div className="header-actions">
          <button className="new-template-btn">
            <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
              <line x1="12" y1="5" x2="12" y2="19" stroke="currentColor" strokeWidth="2"/>
              <line x1="5" y1="12" x2="19" y2="12" stroke="currentColor" strokeWidth="2"/>
            </svg>
            New Template
          </button>
          
          <button className="close-templates-btn" onClick={onClose}>
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none">
              <line x1="18" y1="6" x2="6" y2="18" stroke="currentColor" strokeWidth="2"/>
              <line x1="6" y1="6" x2="18" y2="18" stroke="currentColor" strokeWidth="2"/>
            </svg>
          </button>
        </div>
      </div>

      <div className="templates-content">
        <div className="templates-list">
          {templates.map(template => (
            <div
              key={template.id}
              className={`template-item ${selectedTemplate?.id === template.id ? 'selected' : ''}`}
              onClick={() => handleTemplateClick(template)}
            >
              <div className="template-header">
                <div className="template-icon">
                  {getCategoryIcon(template.category)}
                </div>
                <div className="template-info">
                  <h4>{template.title}</h4>
                  <div className="template-actions">
                    <button className="template-action-btn" title="View">
                      <svg width="14" height="14" viewBox="0 0 24 24" fill="none">
                        <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z" stroke="currentColor" strokeWidth="2"/>
                        <circle cx="12" cy="12" r="3" stroke="currentColor" strokeWidth="2"/>
                      </svg>
                    </button>
                    <button className="template-action-btn" title="Edit">
                      <svg width="14" height="14" viewBox="0 0 24 24" fill="none">
                        <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7" stroke="currentColor" strokeWidth="2"/>
                        <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z" stroke="currentColor" strokeWidth="2"/>
                      </svg>
                    </button>
                  </div>
                </div>
              </div>
              
              <div className="template-preview">
                {template.content.substring(0, 120)}...
              </div>
            </div>
          ))}
        </div>

        {selectedTemplate && (
          <div className="template-preview-panel">
            <div className="preview-header">
              <h3>{selectedTemplate.title}</h3>
              <span className="template-category">{selectedTemplate.category}</span>
            </div>
            
            <div className="preview-content">
              <div className="message-preview">
                {selectedTemplate.content
                  .replace('{ClientName}', contact.name)
                  .replace('{Organization}', 'Smith Medical Clinic')
                  .replace('{Date}', 'tomorrow')
                  .replace('{Time}', '2:00 PM')
                }
              </div>
            </div>
            
            <div className="preview-actions">
              <button 
                className="ai-suggest-btn"
                title="Get AI suggestions for this template"
              >
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                  <path d="M12 2L2 7L12 12L22 7L12 2Z" stroke="currentColor" strokeWidth="2"/>
                  <path d="M2 17L12 22L22 17" stroke="currentColor" strokeWidth="2"/>
                  <path d="M2 12L12 17L22 12" stroke="currentColor" strokeWidth="2"/>
                </svg>
                AI Suggest
              </button>
              
              <button 
                className="use-template-btn"
                onClick={handleUseTemplate}
              >
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none">
                  <path d="M22 2L11 13" stroke="currentColor" strokeWidth="2"/>
                  <polygon points="22,2 15,22 11,13 2,9 22,2" stroke="currentColor" strokeWidth="2"/>
                </svg>
                Send via WhatsApp
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MessageTemplates;
