import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import SplashScreen from './components/SplashScreen';
import Dashboard from './pages/Dashboard';
import Settings from './pages/Settings';
import MessageHistory from './pages/MessageHistory';
import './App.css';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [dbInitialized, setDbInitialized] = useState(false);

  useEffect(() => {
    const initApp = async () => {
      try {
        // Check if we're running in Electron
        const isElectron = window.require && window.process && window.process.type;
        
        if (isElectron) {
          // Initialize database only in Electron
          console.log('Initializing CuraLink database...');
          await initializeDatabase();
          setDbInitialized(true);
          console.log('Database initialized successfully');
        } else {
          // Running in browser - skip database initialization
          console.log('Running in browser mode - database disabled');
          setDbInitialized(false);
        }
        
        // Show splash screen for 2 seconds minimum
        const timer = setTimeout(() => {
          setIsLoading(false);
        }, 2000);

        return () => clearTimeout(timer);
      } catch (error) {
        console.error('Failed to initialize app:', error);
        // Still show the app even if database fails
        setTimeout(() => {
          setIsLoading(false);
        }, 2000);
      }
    };

    initApp();
  }, []);

  if (isLoading) {
    return <SplashScreen />;
  }

  return (
    <Router>
      <div className="app">
        <Routes>
          <Route 
            path="/" 
            element={<Dashboard />} 
          />
          <Route 
            path="/settings" 
            element={<Settings />} 
          />
          <Route 
            path="/history" 
            element={<MessageHistory />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
