import React from 'react';

const CuraLinkLogo = ({ size = 32, showText = true, className = "" }) => {
  return (
    <div className={`curalink-logo ${className}`} style={{ display: 'flex', alignItems: 'center', gap: showText ? '12px' : '0' }}>
      {/* CuraLink Symbol - Interlocking S shapes */}
      <svg 
        width={size} 
        height={size} 
        viewBox="0 0 100 100" 
        fill="none"
      >
        <path
          d="M25 15C15 15 7 23 7 33C7 43 15 51 25 51H45C55 51 63 59 63 69C63 79 55 87 45 87H25C15 87 7 79 7 69"
          stroke="currentColor"
          strokeWidth="8"
          strokeLinecap="round"
          fill="none"
        />
        <path
          d="M75 85C85 85 93 77 93 67C93 57 85 49 75 49H55C45 49 37 41 37 31C37 21 45 13 55 13H75C85 13 93 21 93 31"
          stroke="currentColor"
          strokeWidth="8"
          strokeLinecap="round"
          fill="none"
        />
      </svg>
      
      {/* CuraLink Text */}
      {showText && (
        <span 
          style={{ 
            fontSize: size * 0.6,
            fontWeight: 600,
            color: 'currentColor',
            fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif'
          }}
        >
          CuraLink
        </span>
      )}
    </div>
  );
};

export default CuraLinkLogo;
